import { app, BrowserWindow, ipcMain, Tray, Menu } from 'electron';
import keytar from 'keytar';
import path from 'path';

// This allows TypeScript to pick up the magic constants that's auto-generated by Forge's Webpack
// plugin that tells the Electron app where to look for the Webpack-bundled app code (depending on
// whether you're running in development or production).
declare const MAIN_WINDOW_WEBPACK_ENTRY: string;
declare const MAIN_WINDOW_PRELOAD_WEBPACK_ENTRY: string;

// Handle creating/removing shortcuts on Windows when installing/uninstalling.
if (require('electron-squirrel-startup')) {
  app.quit();
}

let tray: Tray | null = null;

const createTray = (): void => {
  const iconPath = path.join(__dirname, 'assets/icon.png'); 
  tray = new Tray(iconPath);
  
  const contextMenu = Menu.buildFromTemplate([
    { label: 'please wait', enabled: false }
  ]);
  
  tray.setToolTip('pzdeploys');
  tray.setContextMenu(contextMenu);
  // Optional: when clicking the tray icon, show the context menu.
  tray.on('click', () => {
    tray?.popUpContextMenu();
  });
};

const createWindow = (): void => {
  // Create the browser window.
  const mainWindow = new BrowserWindow({
    height: 600,
    width: 800,
    webPreferences: {
      preload: MAIN_WINDOW_PRELOAD_WEBPACK_ENTRY,
    },
  });

  // and load the index.html of the app.
  mainWindow.loadURL(MAIN_WINDOW_WEBPACK_ENTRY);

  // Open the DevTools.
  mainWindow.webContents.openDevTools();
};

ipcMain.handle('store-credentials', async (event, data) => {
  const { renderApiPassword, vercelApiKey } = data;
  try {
    // Store the Render.com API password securely
    await keytar.setPassword('MyElectronApp-Render', 'renderApiPassword', renderApiPassword);
    // Store the Vercel API key securely
    await keytar.setPassword('MyElectronApp-Vercel', 'vercelApiKey', vercelApiKey);
    return { success: true };
  } catch (error) {
    console.error('Error storing credentials:', error);
    throw error;
  }
});

ipcMain.handle('get-credentials', async () => {
  // Retrieve the credentials from secure storage
  const renderApiPassword = await keytar.getPassword('MyElectronApp-Render', 'renderApiPassword');
  const vercelApiKey = await keytar.getPassword('MyElectronApp-Vercel', 'vercelApiKey');
  return { renderApiPassword, vercelApiKey };
});

// This method will be called when Electron has finished
// initialization and is ready to create browser windows.
// Some APIs can only be used after this event occurs.
app.on('ready', () => {
  createTray();
  createWindow();
});

// Quit when all windows are closed, except on macOS. There, it's common
// for applications and their menu bar to stay active until the user quits
// explicitly with Cmd + Q.
app.on('window-all-closed', () => {
  if (process.platform !== 'darwin') {
    app.quit();
  }
});

app.on('activate', () => {
  // On OS X it's common to re-create a window in the app when the
  // dock icon is clicked and there are no other windows open.
  if (BrowserWindow.getAllWindows().length === 0) {
    createWindow();
  }
});

